import React, { useRef } from 'react';
import { Link } from 'react-router-dom';
import css from './HeroHome.module.css'; // Используем CSS-модули

const HeroHome = () => {
  const decorItemRef = useRef(null);

  const resetAnimation = () => {
    decorItemRef.current.style.transition = 'none';
    decorItemRef.current.classList.remove(css.slideIn, css.slideOut);
    void decorItemRef.current.offsetWidth;
    decorItemRef.current.style.transition = '';
  };

  const handleMouseEnter = () => {
    resetAnimation(); // Сброс анимации
    decorItemRef.current.classList.add(css.slideIn); // Запуск анимации слева направо
  };

  const handleMouseLeave = () => {
    decorItemRef.current.classList.remove(css.slideIn);
    decorItemRef.current.classList.add(css.slideOut); // Анимация вправо

    const onTransitionEnd = () => {
      resetAnimation(); // Сброс состояния после завершения анимации
      decorItemRef.current.removeEventListener(
        'transitionend',
        onTransitionEnd
      ); // Удаление слушателя
    };

    decorItemRef.current.addEventListener('transitionend', onTransitionEnd); // Добавление слушателя окончания анимации
  };

  return (
    <section className={css.heroHome}>
      <div className={css.heroContent}>
        <div className={css.heroWrap}>
          <h1 className={css.heroTitle}>
            Buy TikTok{' '}
            <span className={css.pinkText}>Likes, with Instant Delivery</span>
          </h1>
          <p className={css.heroText}>
            Don't settle for fake users. StellarLikes delivers followers, likes,
            and views from real users, delivered instantly. At the lowest
            possible prices.
          </p>
          <Link
            to="/"
            className={css.heroLink}
            onMouseEnter={handleMouseEnter}
            onMouseLeave={handleMouseLeave}
          >
            <span className={css.linkText}>Get 50 Free Likes</span>
            <span className={css.decor}>
              <span className={css.decorItem} ref={decorItemRef}></span>
            </span>
          </Link>
        </div>
      </div>
    </section>
  );
};

export default HeroHome;
