import { useState } from 'react';
import css from './CheckoutBtn.module.css';
import { payCheckout } from '../../Api/payCheckout';

const CheckoutButton = ({ fields }) => {
  const [error, setError] = useState(null);

  const {
    country,
    shop_name,
    product_id,
    email,
    price,
    productService,
    quantity,
    custom_link,
  } = fields;

  const handleClick = async () => {
    try {
      // Вызов функции запроса с передачей данных
      const data = await payCheckout({
        country,
        shop_name,
        product_id,
        email,
        price,
        productService,
        quantity,
        custom_link,
        success_url:
          'https://sergey4857.github.io/StellarLikes/OrderConfirmation',
        fail_url: 'https://sergey4857.github.io/StellarLikes/OrderConfirmation',
        cancel_url:
          'https://sergey4857.github.io/StellarLikes/OrderConfirmation',
      });

      // Проверка успешного ответа и редирект
      if (data && data.redirect_url) {
        window.location.href = data.redirect_url;
      } else {
        setError('Ошибка при получении ссылки для оплаты.');
      }
    } catch (error) {
      setError(error.message);
    }
  };

  return (
    <>
      <button className={css.checkoutButton} onClick={handleClick}>
        <span>Proceed to secure checkout</span>
      </button>
      {error && <p style={{ color: 'red' }}>{error}</p>}
    </>
  );
};

export default CheckoutButton;
