import React, { useEffect, useRef } from 'react';
import { Link } from 'react-router-dom';
import css from './HeroHome.module.css'; // Используем CSS-модули
import { gsap } from 'gsap';

const HeroHome = () => {
  const linkRef = useRef(null);
  const decorItemRef = useRef(null);

  useEffect(() => {
    const link = linkRef.current;
    const decorItem = decorItemRef.current;

    const handleMouseEnter = () => {
      // Установить начальную позицию элемента слева
      gsap.set(decorItem, { translateX: '-100%' });

      gsap.to(decorItem, {
        translateX: '0%', // Анимация выезда к центру
        duration: 0.4,
        ease: 'power2.out', // Плавное появление
      });
    };

    const handleMouseLeave = () => {
      gsap.to(decorItem, {
        translateX: '100%', // Улетает вправо за пределы ссылки
        duration: 0.4,
        ease: 'power2.in', // Плавный уход
      });
    };

    if (link) {
      link.addEventListener('mouseenter', handleMouseEnter);
      link.addEventListener('mouseleave', handleMouseLeave);
    }

    return () => {
      if (link) {
        link.removeEventListener('mouseenter', handleMouseEnter);
        link.removeEventListener('mouseleave', handleMouseLeave);
      }
    };
  }, []);
  return (
    <section className={css.heroHome}>
      <div className={css.heroContent}>
        <div className={css.heroWrap}>
          <h1 className={css.heroTitle}>
            Buy TikTok{' '}
            <span className={css.pinkText}>Likes, with Instant Delivery</span>
          </h1>
          <p className={css.heroText}>
            Don't settle for fake users. StellarLikes delivers followers, likes,
            and views from real users, delivered instantly. At the lowest
            possible prices.
          </p>
          <Link to="/" className={css.heroLink} ref={linkRef}>
            <span className={css.linkText}>Стать партнёром</span>
            <span className={css.decor}>
              <span ref={decorItemRef} className={css.decorItem}></span>
            </span>
          </Link>
        </div>
      </div>
    </section>
  );
};

export default HeroHome;
