import React, { useState } from 'react';
import css from './Review.module.css'; // Подключаем CSS-модуль

const ReviewForm = ({ setShowReviewForm }) => {
  const [rating, setRating] = useState(0); // Управление состоянием рейтинга

  return (
    <form className={css.ReviewForm}>
      <button
        type="button"
        className={css.ReviewCloseButton}
        onClick={() => {
          setShowReviewForm(false);
        }}
      ></button>

      <div className={css.ReviewFormBlock}>
        <label htmlFor="name">Your Name</label>
        <input type="text" id="name" name="name" placeholder="Name" />
      </div>

      <div className={css.ReviewFormBlock}>
        <label htmlFor="email">Your Email</label>
        <input
          type="text"
          id="email"
          name="email"
          placeholder="email@example.com"
        />
      </div>

      <div className={css.ReviewFormBlock}>
        <label htmlFor="review">Your review</label>
        <textarea id="review" name="review" placeholder="review" />
      </div>

      <div className={css.ratingBlock}>
        {[1, 2, 3, 4, 5].map(star => (
          <span
            key={star}
            className={`${css.star} ${rating >= star ? css.filled : ''}`} // Применение класса в зависимости от рейтинга
            onClick={() => setRating(star)} // Установка рейтинга при клике
          >
            ★
          </span>
        ))}
        <span className={css.ratingCounter}>{rating}/5</span>{' '}
        {/* Добавляем счетчик */}
      </div>

      <button className={css.reviewSubmit} type="button">
        Submit Review
      </button>
    </form>
  );
};

export default ReviewForm;
