import { createPortal } from 'react-dom';
import { useEffect, useRef, useState } from 'react';
import css from './BurgerMenu.module.css';
import { Link } from 'react-router-dom';
import { gsap } from 'gsap';

export default function BurgerMenu({ setOpenedModal, openedModal }) {
  const [activeBlockIndex, setActiveBlockIndex] = useState(0);
  const burgerRoot = document.querySelector('#burger-root');

  const menuRef = useRef(null);
  const backgroundRef = useRef(null);

  useEffect(() => {
    const tl = gsap.timeline({ defaults: { ease: 'power2.out' } });

    if (openedModal) {
      // Устанавливаем начальные состояния
      gsap.set(backgroundRef.current, { x: '100%' });
      gsap.set(menuRef.current, { x: '-100%' });

      // Анимируем фон и меню одновременно
      tl.to(backgroundRef.current, { x: '0%', duration: 0.5 });
      tl.to(menuRef.current, { x: '0%', duration: 0.5 }, '<');
    } else {
      // Анимируем выход меню и фона одновременно
      tl.to(menuRef.current, { x: '-100%', duration: 0.5, ease: 'power2.in' });
      tl.to(
        backgroundRef.current,
        { x: '100%', duration: 0.5, ease: 'power2.in' },
        '<'
      );
    }

    // Очистка при размонтировании или повторном рендере
    return () => {
      tl.kill();
    };
  }, [openedModal]);

  const toggleBlock = index => {
    setActiveBlockIndex(activeBlockIndex === index ? null : index);
  };

  const menuItems = [
    {
      title: 'Services',
      links: [
        { to: '/', text: 'Buy TikTok', highlight: 'Likes' },
        {
          to: '/buy-tiktok-followers',
          text: 'Buy TikTok',
          highlight: 'Followers',
        },
        { to: '/buy-tiktok-views', text: 'Buy TikTok', highlight: 'Views' },
      ],
    },
    {
      title: 'Free Tools',
      links: [
        { to: '/', text: 'Free TikTok Video Downloader' },
        { to: '/', text: 'Free TikTok Likes' },
        { to: '/buy-tiktok-views', text: 'Free TikTok Views' },
        {
          to: '/buy-tiktok-followers#freeFollowers',
          text: 'Free TikTok Followers',
        },
      ],
    },
    {
      title: 'About',
      links: [
        { to: '/', text: 'Privacy Policy' },
        { to: '/', text: 'Terms of Use' },
        { to: '/', text: 'Live Support' },
      ],
    },
  ];

  return createPortal(
    <div className={css.BurgerNavWrap}>
      <div className={css.BurgerNavContainer}>
        {/* Background element */}
        <div ref={backgroundRef} className={css.Background}></div>

        {/* Menu element */}
        <div ref={menuRef} className={css.BurgerNav}>
          {menuItems.map((item, index) => (
            <div className={css.burgerWrapper} key={index}>
              <div
                className={`${css.blockTitle} ${
                  activeBlockIndex === index ? css.active : ''
                }`}
                onClick={() => toggleBlock(index)}
              >
                {item.title}
              </div>

              {activeBlockIndex === index && (
                <div className={css.burgerBlock}>
                  {item.links.map((link, linkIndex) => (
                    <Link
                      onClick={() => {
                        setOpenedModal(false);
                      }}
                      key={linkIndex}
                      className={css.burgerNavLink}
                      to={link.to}
                    >
                      <span className={css.whiteText}>{link.text}</span>{' '}
                      <span
                        className={
                          link.highlight === 'Likes'
                            ? css.pinkText
                            : link.highlight === 'Followers'
                            ? css.orangeText
                            : link.highlight === 'Views'
                            ? css.greenText
                            : ''
                        }
                      >
                        {link.highlight}
                      </span>
                    </Link>
                  ))}
                </div>
              )}
            </div>
          ))}
        </div>
      </div>
    </div>,
    burgerRoot
  );
}
