// import { useLocation } from 'react-router-dom';
import { useLocation } from 'react-router-dom';
import css from './SelectPost.module.css';
import { useEffect, useState } from 'react';
import FetchTikTokLikes from 'Api/FetchTikTokLikes';
import FetchUserInstagramPosts from 'Api/FetchUserInstagramPosts';

const SelectPost = () => {
  const location = useLocation();
  const { id } = location.state;
  console.log(id);
  // const location = useLocation();
  // const selectedProduct = location.state?.product;

  useEffect(() => {
    const fetchData = async () => {
      await FetchUserInstagramPosts(id);
    };

    fetchData();
  }, []);

  const [selectedOption, setSelectedOption] = useState('1');

  const handleOptionChange = event => {
    setSelectedOption(event.target.value);
  };
  return (
    <div className={css.selectPostsWrap}>
      <div className="selectPostsFirst">
        <h1>
          Select<span>Post</span>
        </h1>
        <div className="selectPostsInfo">
          Select <span>1 Post / 50 likes per post</span>
        </div>
        {/* <div className={css.checkoutForm}>
        <label
          className={`${css.radioLabel} ${
            selectedOption === '1' ? css.active : ''
          } ${css.radioCredit}`}
        >
          <input
            type="radio"
            name="option"
            value="1"
            checked={selectedOption === '1'}
            onChange={handleOptionChange}
            className={css.radioInput}
          />
          <span className={css.radioCustom}></span>
          Credit / Debit Card
        </label>

        <label
          className={`${css.radioLabel} ${
            selectedOption === '2' ? css.active : ''
          } ${css.radioCrypto}`}
        >
          <input
            type="radio"
            name="option"
            value="2"
            checked={selectedOption === '2'}
            onChange={handleOptionChange}
            className={css.radioInput}
          />
          <span className={css.radioCustom}></span>
          Crypto
        </label>
      </div> */}
        {/* 
      {selectedProduct ? (
        <p>Proceeding with: {selectedProduct}</p>
      ) : (
        <p>No product selected</p>
      )} */}
      </div>
      <div className="selectPostsSecond"></div>
    </div>
  );
};

export default SelectPost;
