import React, { useEffect, useRef } from 'react';
import { gsap } from 'gsap';
import css from './HeroHome.module.css';
import { Link } from 'react-router-dom';

const HeroHome = () => {
  const heroTitleRef = useRef(null);
  const heroTextRef = useRef(null);
  const heroLinkRef = useRef(null);

  const linkRef = useRef(null);

  const handleMouseEnter = () => {
    linkRef.current.classList.remove('leave');
  };

  const handleMouseLeave = () => {
    linkRef.current.classList.add('leave');
    setTimeout(() => {
      linkRef.current.classList.remove('leave');
    }, 500); // Длительность анимации
  };

  useEffect(() => {
    const tl = gsap.timeline();
    tl.from(heroLinkRef.current, {
      duration: 0.5,
      scale: 1.3,
      ease: 'power1.inOut',
      repeat: 3,
    }).to(heroLinkRef.current, {
      duration: 0.5,
      scale: 1,
      repeat: 3,
      ease: 'power1.inOut',
    });
  }, []);

  return (
    <section className={css.heroHome}>
      <div className={css.heroContent}>
        <div className={css.heroWrap}>
          <h1 className={css.heroTitle} ref={heroTitleRef}>
            Buy TikTok{' '}
            <span className="pinkText">Likes, with Instant Delivery</span>
          </h1>
          <p className={css.heroText} ref={heroTextRef}>
            Don't settle for fake users. StellarLikes delivers followers, likes,
            and views from real users, delivered instantly. At the lowest
            possible prices.
          </p>
          <Link to="/" className="heroLink">
            Get 50 Free Likes
            <span className="decor">
              <span className="decor-item"></span>
            </span>
          </Link>
        </div>
      </div>
    </section>
  );
};

export default HeroHome;
