import { useLocation } from 'react-router-dom';
import css from './Checkout.module.css';
import { useState } from 'react';

const Checkout = () => {
  const location = useLocation();
  const selectedProduct = location.state?.product;

  const [selectedOption, setSelectedOption] = useState('');

  const handleOptionChange = event => {
    setSelectedOption(event.target.value);
  };
  return (
    <>
      <div className="checkoutWrap">
        <div className="checkoutFirstBlock">
          <h1 className="checkoutTitle">Checkout</h1>
          <div className="checkoutMethodTitle">Payment method</div>
          <div className="checkoutForm">
            <label>
              <input
                type="radio"
                name="option"
                value="1"
                checked={selectedOption === '1'}
                onChange={handleOptionChange}
                className="checkoutCredit"
              />
              Credit / Debit Card
            </label>

            <label>
              <input
                type="radio"
                name="option"
                value="2"
                checked={selectedOption === '2'}
                onChange={handleOptionChange}
                className="checkoutCrypto"
              />
              Crypto
            </label>
          </div>
          <button className="checkoutButton">
            Procceed to secure checkout
          </button>
          <div className="checkoutInfo">
            All transactions are secure and encrypted
          </div>
        </div>
        <div className="checkoutSecondBlock">
          <div className="priceInfo">
            Total to pay:
            <span className="price">$7.42</span>
          </div>
          <div className="serviceBlock">
            <div className="serviceTitle">Service</div>
            <div className="serviceProduct">1100 TikTok Followers</div>
          </div>
        </div>
      </div>

      {selectedProduct ? (
        <p>Proceeding with: {selectedProduct}</p>
      ) : (
        <p>No product selected</p>
      )}
    </>
  );
};

export default Checkout;
