import React, { useRef } from 'react';
import { Link } from 'react-router-dom';
import css from './HeroHome.module.css'; // Используем CSS-модули

const HeroHome = () => {
  const buttonRef = useRef(null);
  const decorItemRef = useRef(null);

  useEffect(() => {
    const button = buttonRef.current;
    const decorItem = decorItemRef.current;

    const handleMouseEnter = () => {
      gsap.to(button, {
        scaleX: 1.03,
        scaleY: 0.98,
        duration: 1,
        ease: 'elastic.out(1, 0.3)',
      });

      gsap.fromTo(
        decorItem,
        { translateX: '-100%' },
        {
          translateX: 0,
          duration: 0.4,
          stagger: 0.08,
        }
      );
    };

    const handleMouseLeave = () => {
      gsap.to(button, {
        scaleX: 1,
        scaleY: 1,
        duration: 1,
        ease: 'elastic.out(1, 0.3)',
      });

      gsap.to(decorItem, {
        translateX: '100%',
        duration: 0.4,
        stagger: 0.08,
      });
    };

    if (button) {
      button.addEventListener('mouseenter', handleMouseEnter);
      button.addEventListener('mouseleave', handleMouseLeave);
    }

    return () => {
      if (button) {
        button.removeEventListener('mouseenter', handleMouseEnter);
        button.removeEventListener('mouseleave', handleMouseLeave);
      }
    };
  }, []);
  return (
    <section className={css.heroHome}>
      <div className={css.heroContent}>
        <div className={css.heroWrap}>
          <h1 className={css.heroTitle}>
            Buy TikTok{' '}
            <span className={css.pinkText}>Likes, with Instant Delivery</span>
          </h1>
          <p className={css.heroText}>
            Don't settle for fake users. StellarLikes delivers followers, likes,
            and views from real users, delivered instantly. At the lowest
            possible prices.
          </p>
          <Link to="/" className={css.heroLink} ref={buttonRef}>
            <span className={css.linkText}>Get 50 Free Likes</span>
            <span className={css.decor}>
              <span ref={decorItemRef} className={css.decorItem}></span>
            </span>
          </Link>
        </div>
      </div>
    </section>
  );
};

export default HeroHome;
