import React, { useEffect, useRef, useState } from 'react';
import { Outlet, useNavigate } from 'react-router-dom';
import { gsap } from 'gsap';
import Benefits from 'components/Benefits/Benefits';
import Rating from 'components/Rating/Rating';
import Features from 'components/Features/Features';
import Customers from 'components/Customers/Customers';
import FaqBlock from 'components/Faq/FaqBlock';
import TikTokLikes from './TikTokLikes';
import CustomQuantity from 'components/CustomQuantiy/CustomQuantity';
import HeroHome from 'components/HeroHome/HeroHome';
import FreeLikes from 'components/FreeLikes/FreeLikes';
import Available from 'components/Available/Available';
import css from './TikTokLikesPage.module.css';
import tikTokLikes from '../../icons/tiktokLikes.svg';

const TikTokLikesPage = ({ tiktokLikesData }) => {
  const navigate = useNavigate();
  const linkRef = useRef(null);
  const decorItemRefs = useRef([]);
  const [showCustomQuantity, setShowCustomQuantity] = useState(false);
  const [showPackages, setShowPackages] = useState(true);
  const [tiktoklikes, setTiktoklikes] = useState([]);
  const [selectedPrice, setSelectedPrice] = useState(null);
  const [customQuantity, setCustomQuantity] = useState(0);
  const [customPrice, setCustomPrice] = useState({
    newPrice: 0,
    oldPrice: 0,
    savings: 0,
    discountPercent: 0,
  });

  // Get base price and discount levels
  const basePricePerUnit = parseFloat(tiktokLikesData[0].price);
  const discountLevels = tiktokLikesData[0].discount_levels
    .slice()
    .sort((a, b) => a.quantity - b.quantity);

  // Initialize packages and set the first one as active
  useEffect(() => {
    if (tiktokLikesData && tiktokLikesData.length > 0) {
      const packages = tiktokLikesData[0].preset_packages.map((pkg, index) => ({
        ...pkg,
        quantity: parseInt(pkg.quantity),
        discountPercent: parseFloat(pkg.discount.replace('%', '')) / 100,
        active: index === 0,
      }));
      setTiktoklikes(packages);
      setSelectedPrice(packages[0]);
    }
  }, [tiktokLikesData]);

  // Handle package selection
  const toggleLikes = index => {
    setTiktoklikes(prevLikes =>
      prevLikes.map((likes, i) => ({
        ...likes,
        active: i === index,
      }))
    );
    setSelectedPrice(tiktoklikes[index]);
  };

  // Calculate prices based on the selected package
  let oldPrice = 0;
  let newPrice = 0;
  let savings = 0;

  if (selectedPrice && showPackages) {
    oldPrice = selectedPrice.quantity * basePricePerUnit;
    newPrice = oldPrice * (1 - selectedPrice.discountPercent);
    savings = oldPrice - newPrice;
  }

  // Function to handle custom quantity change
  const handleCustomQuantityChange = quantity => {
    setCustomQuantity(quantity);
    // Find applicable discount level
    let applicableDiscount = 0;
    for (let i = discountLevels.length - 1; i >= 0; i--) {
      if (quantity >= discountLevels[i].quantity) {
        applicableDiscount = discountLevels[i].discount;
        break;
      }
    }
    // Calculate prices
    const oldPrice = quantity * basePricePerUnit;
    const newPrice = oldPrice * (1 - applicableDiscount / 100);
    const savings = oldPrice - newPrice;
    setCustomPrice({
      newPrice,
      oldPrice,
      savings,
      discountPercent: applicableDiscount,
    });
  };

  // Animations (existing code)
  useEffect(() => {
    const link = linkRef.current;
    const decorItems = decorItemRefs.current;

    const handleMouseEnter = () => {
      gsap.to(link, {
        scaleX: 1.03,
        scaleY: 0.98,
        duration: 1,
        ease: 'elastic.out(1, 0.3)',
      });

      gsap.fromTo(
        decorItems,
        { translateX: '-100%' },
        {
          translateX: 0,
          duration: 0.4,
          stagger: 0.08,
        }
      );
    };

    const handleMouseLeave = () => {
      gsap.to(link, {
        scaleX: 1,
        scaleY: 1,
        duration: 1,
        ease: 'elastic.out(1, 0.3)',
      });

      gsap.to(decorItems, {
        translateX: '100%',
        duration: 0.4,
        stagger: 0.08,
      });
    };

    if (link) {
      link.addEventListener('mouseenter', handleMouseEnter);
      link.addEventListener('mouseleave', handleMouseLeave);
    }

    return () => {
      if (link) {
        link.removeEventListener('mouseenter', handleMouseEnter);
        link.removeEventListener('mouseleave', handleMouseLeave);
      }
    };
  }, []);

  // ... (Your existing FAQ data and other components)

  return (
    <>
      {/* ... Your existing components like Outlet, HeroHome, etc. */}
      <section className={css.buyLikes}>
        {/* ... Your existing code */}
        {showPackages && (
          <div className={css.buyLikesQuantityBlock}>
            {tiktoklikes.map((data, index) => (
              <TikTokLikes
                data={data}
                index={index}
                key={index}
                color="violet"
                toggleLikes={toggleLikes}
              />
            ))}
          </div>
        )}

        {showCustomQuantity && (
          <CustomQuantity
            discountLevels={discountLevels}
            basePricePerUnit={basePricePerUnit}
            onQuantityChange={handleCustomQuantityChange}
            customPrice={customPrice}
            customQuantity={customQuantity}
          />
        )}

        <div className={css.priceBlock}>
          {showPackages && selectedPrice && (
            <div className={css.priceContent}>
              <div className={css.priceWrap}>
                <div className={css.mainPrice}>${newPrice.toFixed(2)}</div>
                <div className={css.oldPrice}>${oldPrice.toFixed(2)}</div>
              </div>
              <div className={css.savings}>
                You’re saving{' '}
                <span className={css.savingsPrice}>
                  ${savings.toFixed(2)} (
                  {(selectedPrice.discountPercent * 100).toFixed(0)}% off)
                </span>
              </div>
            </div>
          )}
          {showCustomQuantity && customQuantity > 0 && (
            <div className={css.priceContent}>
              <div className={css.priceWrap}>
                <div className={css.mainPrice}>
                  ${customPrice.newPrice.toFixed(2)}
                </div>
                <div className={css.oldPrice}>
                  ${customPrice.oldPrice.toFixed(2)}
                </div>
              </div>
              <div className={css.savings}>
                You’re saving{' '}
                <span className={css.savingsPrice}>
                  ${customPrice.savings.toFixed(2)} (
                  {customPrice.discountPercent}% off)
                </span>
              </div>
            </div>
          )}

          <button
            className={css.buyLink}
            ref={linkRef}
            onClick={() =>
              navigate('getStarted', {
                state: showPackages
                  ? { selectedPrice }
                  : { customPrice, customQuantity },
              })
            }
          >
            <span className={css.linkText}>Buy Now</span>
            <span className={css.decor}>
              <span
                ref={el => (decorItemRefs.current[0] = el)}
                className={css.decorItem}
              ></span>
              <span
                ref={el => (decorItemRefs.current[1] = el)}
                className={css.decorItem}
              ></span>
            </span>
          </button>
        </div>
      </section>
      {/* ... Your existing components like Available, Benefits, etc. */}
    </>
  );
};

export default TikTokLikesPage;
