import React, { useState } from 'react';
import axios from 'axios';
import css from './CouponButton.module.css';

const consumer_key = process.env.REACT_APP_TEST_CHECKOUT_CONSUMER_KEY;
const consumer_secret = process.env.REACT_APP_TEST_CHECKOUT_CONSUMER_SECRET_KEY;

const CouponButton = ({ price, applyDiscount }) => {
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const [couponCode, setCouponCode] = useState('');

  const FetchAllCoupons = async () => {
    try {
      setLoading(true);
      setError(null);

      const response = await axios.get(
        `https://testgraming.net/wp-json/wc/v3/coupons?consumer_key=${consumer_key}&consumer_secret=${consumer_secret}`
      );

      const coupons = response.data;
      const matchedCoupon = coupons.find(
        coupon => coupon.code.toLowerCase() === couponCode.toLowerCase()
      );

      if (matchedCoupon) {
        if (matchedCoupon.usage_count < matchedCoupon.usage_limit) {
          const discountValue = matchedCoupon.amount;
          const newPrice = (price * (100 - discountValue)) / 100;

          applyDiscount(newPrice.toFixed(2), discountValue);
        } else {
          setError('Coupon usage limit reached.');
        }
      } else {
        setError('Invalid coupon code.');
      }
    } catch (error) {
      console.error('Error fetching coupons:', error);
      setError('An error occurred while applying the coupon.');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className={css.couponBlock}>
      <input
        className={css.couponInpt}
        type="text"
        placeholder="Enter code"
        value={couponCode}
        onChange={e => setCouponCode(e.target.value)}
      />
      <button
        className={css.couponSbmt}
        type="button"
        onClick={FetchAllCoupons}
        disabled={loading}
      >
        {loading ? 'Loading...' : 'Apply'}
      </button>
      {error && <p className={css.errorMessage}>{error}</p>}
    </div>
  );
};

export default CouponButton;
