import React, { useState, useEffect } from 'react';
import css from './CustomQuantity.module.css';

const CustomQuantity = ({
  discountLevels,
  basePricePerUnit,
  onQuantityChange,
  customPrice,
  customQuantity,
}) => {
  const [quantity, setQuantity] = useState(customQuantity || 0);
  const [discountPercent, setDiscountPercent] = useState(0);
  const [nextDiscountQuantity, setNextDiscountQuantity] = useState(null);
  const [nextDiscountPercent, setNextDiscountPercent] = useState(null);

  useEffect(() => {
    // Call the parent function to update prices
    onQuantityChange(quantity);

    // Find applicable discount
    let applicableDiscount = 0;
    for (let i = discountLevels.length - 1; i >= 0; i--) {
      if (quantity >= discountLevels[i].quantity) {
        applicableDiscount = discountLevels[i].discount;
        break;
      }
    }
    setDiscountPercent(applicableDiscount);

    // Find next discount level
    let nextLevel = null;
    for (let i = 0; i < discountLevels.length; i++) {
      if (quantity < discountLevels[i].quantity) {
        nextLevel = discountLevels[i];
        break;
      }
    }
    if (nextLevel) {
      setNextDiscountQuantity(nextLevel.quantity - quantity);
      setNextDiscountPercent(nextLevel.discount);
    } else {
      setNextDiscountQuantity(null);
      setNextDiscountPercent(null);
    }
  }, [quantity, discountLevels, onQuantityChange]);

  const handleInputChange = e => {
    let value = parseInt(e.target.value, 10);
    if (isNaN(value) || value < 0) {
      value = 0;
    }
    setQuantity(value);
  };

  return (
    <>
      <div className={css.customQuantitySection}>
        <div className={`${css.customAmountBlock}`}>
          <div className={css.customAmountTitle}>Your Custom Amount</div>
          <input
            className={css.customAmountInput}
            type="number"
            placeholder="Tap Here"
            value={quantity}
            onChange={handleInputChange}
            min="0"
            max="20000"
          />
        </div>
        <div className={css.customDiscountBlock}>
          <div className={`${css.customDiscountTitle}`}>Your Discount</div>
          <div className={`${css.customDiscountPercent}`}>
            {discountPercent}% off
          </div>
        </div>
      </div>
      {nextDiscountQuantity !== null && nextDiscountQuantity > 0 && (
        <div className={css.customQuantityText}>
          You need{' '}
          <span className={css.customQuantitySpan}>{nextDiscountQuantity}</span>{' '}
          more likes for{' '}
          <span className={css.customQuantitySpan}>{nextDiscountPercent}%</span>{' '}
          discount
        </div>
      )}
    </>
  );
};

export default CustomQuantity;
