import React, { useRef } from 'react';
import { Link } from 'react-router-dom';
import css from './HeroHome.module.css'; // Используем CSS-модули

const HeroHome = () => {
  const heroTitleRef = useRef(null);
  const heroTextRef = useRef(null);
  const heroLinkRef = useRef(null);
  const decorItemRef = useRef(null);

  const handleMouseEnter = () => {
    decorItemRef.current.classList.remove(css.slideOut);
    decorItemRef.current.classList.add(css.slideIn);
  };

  const handleMouseLeave = () => {
    decorItemRef.current.classList.remove(css.slideIn);
    decorItemRef.current.classList.add(css.slideOut);

    const onTransitionEnd = () => {
      // После завершения анимации выезда вправо
      decorItemRef.current.classList.remove(css.slideOut);

      // Отключаем переход, чтобы перемещение произошло мгновенно
      decorItemRef.current.style.transition = 'none';

      // Принудительно вызываем reflow, чтобы браузер применил изменения
      void decorItemRef.current.offsetWidth;

      // Включаем переход обратно
      decorItemRef.current.style.transition = '';

      // Удаляем слушатель события
      decorItemRef.current.removeEventListener(
        'transitionend',
        onTransitionEnd
      );
    };

    decorItemRef.current.addEventListener('transitionend', onTransitionEnd);
  };

  return (
    <section className={css.heroHome}>
      <div className={css.heroContent}>
        <div className={css.heroWrap}>
          <h1 className={css.heroTitle} ref={heroTitleRef}>
            Buy TikTok{' '}
            <span className={css.pinkText}>Likes, with Instant Delivery</span>
          </h1>
          <p className={css.heroText} ref={heroTextRef}>
            Don't settle for fake users. StellarLikes delivers followers, likes,
            and views from real users, delivered instantly. At the lowest
            possible prices.
          </p>
          <Link
            to="/"
            className={css.heroLink}
            onMouseEnter={handleMouseEnter}
            onMouseLeave={handleMouseLeave}
            ref={heroLinkRef}
          >
            <span className={css.linkText}>Get 50 Free Likes</span>
            <span className={css.decor}>
              <span className={css.decorItem} ref={decorItemRef}>
                <span className={css.decorText}>Get 50 Free Likes</span>
              </span>
            </span>
          </Link>
        </div>
      </div>
    </section>
  );
};

export default HeroHome;
